mapper.select:

select id, device_id, patient_id, event, details, modify_time, create_time
from device_record
where id = #{id,jdbcType=BIGINT}

select dr.id as id,dr.event as event,dr.details as details,d.id as deviceId,d.config as deviceName,dr.patient_id as patientId,dr.create_time as createTime,d.update_time as updateTime from device d
 LEFT JOIN device_record dr on d.id = dr.device_id
where dr.device_id = #{deviceId,jdbcType=BIGINT}
