select * from exercise_record
    where end_time BETWEEN  #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}
    and patient_id= #{patientId,jdbcType=BIGINT}
    and arthro = #{arthro,jdbcType=INTEGER}
    and postion = #{postion,jdbcType=INTEGER}
    and direction = #{direction,jdbcType=INTEGER}
    ORDER  BY create_time DESC

select * from exercise_record
  where end_time BETWEEN  #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}
  and patient_id= #{patientId,jdbcType=BIGINT}
  and arthro = #{arthro,jdbcType=INTEGER}
  and postion = #{postion,jdbcType=INTEGER}
  and direction = #{direction,jdbcType=INTEGER}
  ORDER  BY create_time DESC

select
er.id as id,u.real_name as patientName,er.record_data as recordData,er.start_time as startTime,er.end_time as endTime,er.arthro as arthro,
er.direction as direction,er.postion as postion,er.start_angle as startAngle,er.end_angle as endAngle,er.geolocation as geolocation,er.sr as sr
from exercise_record er
LEFT JOIN patient p on er.patient_id = p.id
LEFT JOIN user u on p.user_id = u.id
where 1=1

  and er.start_time BETWEEN #{startTime,jdbcType=TIMESTAMP} and #{endTime,jdbcType=TIMESTAMP}


  and u.real_name like concat('%',#{patientName,jdbcType=VARCHAR},'%')


  and er.arthro = #{arthro,jdbcType=VARCHAR}


  and er.postion = #{postion,jdbcType=VARCHAR}


  and er.direction = #{direction,jdbcType=VARCHAR}
