mapper.select:

select id, user_id, score_change, change_value, surplus_score, create_time, update_time
from score
where id = #{id,jdbcType=BIGINT}

select id, user_id, score_change, change_value, surplus_score, create_time, update_time
from score
where user_id = #{user_id,jdbcType=BIGINT}

select u.id as id,u.real_name as userName,u.score_value as scoreValue,s.score_change as scoreChange,s.change_value as changeValue, s.create_time as createTime,s.update_time as updateTime from user u inner join score s on u.id=s.user_id where 1=1
and u.real_name LIKE concat('%',#{userName,jdbcType=VARCHAR}, '%')
and s.create_time BETWEEN #{startTime,jdbcType=TIMESTAMP} AND #{endTime,jdbcType=TIMESTAMP}
